  } else if (str_detect(rights[[1]], phrases[[2]])) {
    status <- "SUCCESS"
    message <- "The document is licensed with a Creative Commons CC-0 license."
  } else {
    status <- "FAILURE"
    message <- "The document is not licensed with a Creative Commons CC-0 or CC-BY license."
  }
}

