def call():
  global output
  global status

  import metadig.variable as mvar

  global attributePrecision
  global attributeName

  maxPrint = 5

  # Check if a measurement precision is present for each attribute.
  if not attributesPresent:
    output = "No attributes present that require measurement precision."
    status = "FAILURE"
    return False

  attributeCount = 0
  missing = []

  attributePrecision = mvar.toUnicode(attributePrecision)
  attributeName = mvar.toUnicode(attributeName)

  if(isinstance(attributePrecision, unicode)):
    attributePrecision = [attributePrecision]

  if(isinstance(attributeName, unicode)):
    attributeName = [attributeName]

  for i in range(0, len(attributePrecision)):
    attributeCount += 1
    precision = attributePrecision[i]
    if(len(attributeName) >= i):
      name = attributeName[i]
      if(name is None):
        name = "#{}".format(i)
    else:
      name = "#{}".format(i)

    # No precision for this attribute was specified
    if(precision is None):
      missing.append(name)
    else:
      # Precision value should be type int
      try:
        val = int(precision)
      except:
        missing.append(name)

  if(len(missing) > 0):
    output = "These {} attributes (of {} total) are missing a precision value or have an invalid value specified: '{}'".format(len(missing), len(attributePrecision), ', '.join(missing[0:maxPrint]))
    if(len(missing) > maxPrint):
      output += u", ..."
    status = "FAILURE"
    return False
  else:
    output = "All {} attributes that require a measurement precision have one defined".format(attributeCount)
    status = "SUCCESS"
    return True
